import string

alfabet = list(string.ascii_lowercase)
    
def kevin():
    return ["a","e","i","o","u"]

def stuart():   #zwraca liste ze spolgloskami
    spolgloski_lista = []   
    
    for letter in alfabet:
        if letter not in kevin():
            spolgloski_lista.append(letter)
                
    return spolgloski_lista

def test(string, kevin_or_stuart):
    string_enum = (list(enumerate(string)))
    
    print(string_enum)
    
    poczatkowe_lista = [x for x,y in string_enum if y in kevin_or_stuart()]
    
    print(poczatkowe_lista)
    
    cyfra = 0
    
    for i in poczatkowe_lista:
        for x in range(len(string)+1):
            wynik = []
            if string[i:x] != "":       #usuwa puste "" ktore dodaja sie do listy przez zakresy 0:0 etc.
                wynik.append(string[i:x])
                cyfra += 1
                print(wynik)
                wynik = []

    return cyfra    
    
def minion_game(string):
    
    string = string.lower()
    
    wynik_kevin = test(string, kevin)
    wynik_stuart = test(string, stuart)
    
    if wynik_stuart > wynik_kevin:
        print("Stuart " + str(wynik_stuart))
    elif wynik_stuart == wynik_kevin:
        print("Draw")
    else:
        print("Kevin " + str(wynik_kevin))
    

minion_game("kurwa bez sensu jebana mac")   
